// Real-time news aggregation service for civil rights issues (via backend proxy)
class NewsAggregatorAPI {
  constructor() {
    // Use backend proxy to avoid exposing API keys
    this.baseURL = '/api/news';
  }

  // Get latest civil rights news
  async getLatestCivilRightsNews(limit = 20) {
    try {
      const query = 'civil+rights+OR+discrimination+OR+police+reform';
      const response = await fetch(`${this.baseURL}/everything?q=${query}&limit=${limit}`);

      if (!response.ok) {
        if (response.status === 503) {
          console.warn('NewsAPI not configured');
          return [];
        }
        throw new Error(`API error: ${response.status}`);
      }

      const data = await response.json();
      return data.articles || [];
    } catch (error) {
      console.error('Error fetching news:', error);
      return [];
    }
  }

  // Get state-specific civil rights news
  async getStateNews(state, limit = 10) {
    try {
      const query = `${state}+civil+rights+OR+${state}+discrimination`;
      const response = await fetch(`${this.baseURL}/everything?q=${encodeURIComponent(query)}&limit=${limit}`);

      if (!response.ok) {
        if (response.status === 503) {
          console.warn('NewsAPI not configured');
          return [];
        }
        throw new Error(`API error: ${response.status}`);
      }

      const data = await response.json();
      return data.articles || [];
    } catch (error) {
      console.error('Error fetching state news:', error);
      return [];
    }
  }

  // Get breaking news alerts
  async getBreakingAlerts(limit = 5) {
    try {
      const response = await fetch(`${this.baseURL}/top-headlines?q=civil+rights&limit=${limit}`);

      if (!response.ok) {
        if (response.status === 503) {
          console.warn('NewsAPI not configured');
          return [];
        }
        throw new Error(`API error: ${response.status}`);
      }

      const data = await response.json();
      return data.articles || [];
    } catch (error) {
      console.error('Error fetching breaking news:', error);
      return [];
    }
  }

  // Subscribe to news updates
  subscribeToNewsUpdates(callback) {
    // Real-time updates would require WebSockets or Server-Sent Events
    // This functionality requires a valid API key and proper backend setup
    console.log('News updates subscription ready (requires API configuration)');
  }
}

export default new NewsAggregatorAPI();

src/components/ui/Button.jsx.content:
import React from 'react';

const variants = {
  primary: 'bg-blue-600 hover:bg-blue-700 text-white',
  secondary: 'bg-slate-600 hover:bg-slate-700 text-white',
  danger: 'bg-red-600 hover:bg-red-700 text-white',
  success: 'bg-green-600 hover:bg-green-700 text-white',
  warning: 'bg-yellow-600 hover:bg-yellow-700 text-white',
  info: 'bg-cyan-600 hover:bg-cyan-700 text-white',
  outline: 'bg-transparent border border-blue-600 text-blue-600 hover:bg-blue-600 hover:text-white',
  ghost: 'bg-transparent text-blue-600 hover:bg-blue-100 dark:hover:bg-slate-800',
};

const sizes = {
  xs: 'py-1 px-2 text-xs',
  sm: 'py-1.5 px-3 text-sm',
  md: 'py-2 px-4 text-base',
  lg: 'py-2.5 px-5 text-lg',
  xl: 'py-3 px-6 text-xl',
};

const Button = ({
  children,
  variant = 'primary',
  size = 'md',
  className = '',
  disabled = false,
  fullWidth = false,
  leftIcon = null,
  rightIcon = null,
  type = 'button',
  onClick,
  ...props
}) => {
  return (
    <button
      type={type}
      className={`
        ${variants[variant] || variants.primary}
        ${sizes[size] || sizes.md}
        ${fullWidth ? 'w-full' : ''}
        rounded-md font-medium transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500
        ${disabled ? 'opacity-50 cursor-not-allowed' : ''}
        ${className}
      `}
      disabled={disabled}
      onClick={onClick}
      {...props}
    >
      <span className="flex items-center justify-center">
        {leftIcon && <span className="mr-2">{leftIcon}</span>}
        {children}
        {rightIcon && <span className="ml-2">{rightIcon}</span>}
      </span>
    </button>
  );
};

export { Button };
export default Button;

src/components/dashboard/UserDashboard.j.content:
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext.jsx';
import { 
  User, 
  BookOpen, 
  Map, 
  Bell, 
  FileText, 
  Settings, 
  LogOut, 
  Bookmark, 
  Clock, 
  AlertCircle,
  CheckCircle,
  X
} from 'lucide-react';
import Button from '../ui/Button';
import Card from '../ui/Card';

const UserDashboard = () => {
  const { user, signOut } = useAuth();
  const [activeTab, setActiveTab] = useState('overview');
  const [savedCases, setSavedCases] = useState([]);
  const [recentSearches, setRecentSearches] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showNotification, setShowNotification] = useState(false);
  const [notificationMessage, setNotificationMessage] = useState('');
  const [notificationType, setNotificationType] = useState('success');

  // Tabs configuration
  const tabs = [
    { id: 'overview', label: 'Overview', icon: <User className="h-5 w-5" /> },
    { id: 'saved', label: 'Saved Cases', icon: <Bookmark className="h-5 w-5" /> },
    { id: 'history', label: 'Search History', icon: <Clock className="h-5 w-5" /> },
    { id: 'notifications', label: 'Notifications', icon: <Bell className="h-5 w-5" /> },
    { id: 'settings', label: 'Settings', icon: <Settings className="h-5 w-5" /> },
  ];

  // Mock data loading
  useEffect(() => {
    const loadUserData = async () => {
      try {
        // Simulate API calls
        await new Promise(resolve => setTimeout(resolve, 500));

        // Mock data
        setSavedCases([
          { id: 1, title: 'Terry v. Ohio', date: '2023-09-15', circuit: 'Supreme Court' },
          { id: 2, title: 'Floyd v. City of New York', date: '2023-08-22', circuit: '2nd Circuit' },
          { id: 3, title: 'NAACP v. Alabama', date: '2023-07-10', circuit: 'Supreme Court' },
        ]);

        setRecentSearches([
          { id: 1, query: 'First Amendment protest rights', date: '2023-09-18', filters: { circuit: '9th Circuit' } },
          { id: 2, query: 'Stop and identify laws', date: '2023-09-15', filters: { state: 'Nevada' } },
          { id: 3, query: 'Qualified immunity', date: '2023-09-10', filters: {} },
        ]);

        setNotifications([
          { id: 1, message: 'New case added to your saved collection', date: '2023-09-19', read: false, type: 'info' },
          { id: 2, message: 'Important update to Terry v. Ohio analysis', date: '2023-09-17', read: true, type: 'update' },
          { id: 3, message: 'Circuit split detected on qualified immunity', date: '2023-09-12', read: false, type: 'alert' },
        ]);
      } catch (error) {
        console.error('Error loading user data:', error);
        showToast('Failed to load user data', 'error');
      } finally {
        setLoading(false);
      }
    };

    loadUserData();
  }, []);

  const showToast = (message, type = 'success') => {
    setNotificationMessage(message);
    setNotificationType(type);
    setShowNotification(true);

    setTimeout(() => {
      setShowNotification(false);
    }, 3000);
  };

  const handleSignOut = async () => {
    try {
      await signOut();
      showToast('Successfully signed out');
    } catch (error) {
      showToast('Failed to sign out', 'error');
    }
  };

  const handleRemoveSavedCase = (id) => {
    setSavedCases(savedCases.filter(item => item.id !== id));
    showToast('Case removed from saved items');
  };

  const handleClearHistory = () => {
    setRecentSearches([]);
    showToast('Search history cleared');
  };

  const handleMarkAllRead = () => {
    setNotifications(notifications.map(item => ({ ...item, read: true })));
    showToast('All notifications marked as read');
  };

  return (
    <div className="bg-slate-900 text-white min-h-screen">
      {/* Toast notification */}
      {showNotification && (
        <div className={`fixed top-4 right-4 z-50 p-4 rounded-lg shadow-lg flex items-center ${
          notificationType === 'success' ? 'bg-green-800 text-green-100' : 
          notificationType === 'error' ? 'bg-red-800 text-red-100' : 
          'bg-blue-800 text-blue-100'
        }`}>
          {notificationType === 'success' ? (
            <CheckCircle className="h-5 w-5 mr-2" />
          ) : (
            <AlertCircle className="h-5 w-5 mr-2" />
          )}
          <span>{notificationMessage}</span>
          <button 
            onClick={() => setShowNotification(false)}
            className="ml-4 text-gray-300 hover:text-white"
          >
            <X className="h-4 w-4" />
          </button>
        </div>
      )}

      <div className="container mx-auto px-4 py-8">
        <div className="flex flex-col md:flex-row gap-6">
          {/* Sidebar */}
          <div className="md:w-64 flex-shrink-0">
            <div className="bg-slate-800 rounded-lg border border-slate-700 overflow-hidden">
              <div className="p-4 border-b border-slate-700">
                <div className="flex items-center">
                  <div className="bg-blue-600 rounded-full w-10 h-10 flex items-center justify-center">
                    <User className="h-6 w-6 text-white" />
                  </div>
                  <div className="ml-3">
                    <h3 className="text-white font-medium">{user?.email || 'User'}</h3>
                    <p className="text-xs text-gray-400">Member since {new Date().toLocaleDateString()}</p>
                  </div>
                </div>
              </div>

              <nav className="p-2">
                {tabs.map(tab => (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id)}
                    className={`w-full flex items-center px-3 py-2 rounded-md text-sm font-medium ${
                      activeTab === tab.id
                        ? 'bg-blue-900/50 text-blue-300'
                        : 'text-gray-300 hover:bg-slate-700 hover:text-white'
                    }`}
                  >
                    <span className="mr-3">{tab.icon}</span>
                    {tab.label}
                    {tab.id === 'notifications' && notifications.some(n => !n.read) && (
                      <span className="ml-auto bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
                        {notifications.filter(n => !n.read).length}
                      </span>
                    )}
                  </button>
                ))}

                <button
                  onClick={handleSignOut}
                  className="w-full flex items-center px-3 py-2 rounded-md text-sm font-medium text-gray-300 hover:bg-slate-700 hover:text-white mt-4"
                >
                  <LogOut className="h-5 w-5 mr-3" />
                  Sign Out
                </button>
              </nav>
            </div>
          </div>

          {/* Main content */}
          <div className="flex-1">
            {loading ? (
              <div className="flex justify-center py-12">
                <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
              </div>
            ) : (
              <>
                {activeTab === 'overview' && (
                  <div>
                    <h2 className="text-2xl font-bold text-white mb-6">Dashboard Overview</h2>

                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
                      <Card
                        title="Saved Cases"
                        icon={<BookOpen className="h-6 w-6 text-blue-400" />}
                        className="bg-slate-800 border-slate-700"
                      >
                        <div className="text-3xl font-bold text-white">{savedCases.length}</div>
                        <p className="text-gray-400 text-sm">Cases in your library</p>
                      </Card>

                      <Card
                        title="Recent Searches"
                        icon={<Clock className="h-6 w-6 text-blue-400" />}
                        className="bg-slate-800 border-slate-700"
                      >
                        <div className="text-3xl font-bold text-white">{recentSearches.length}</div>
                        <p className="text-gray-400 text-sm">Searches in your history</p>
                      </Card>

                      <Card
                        title="Notifications"
                        icon={<Bell className="h-6 w-6 text-blue-400" />}
                        className="bg-slate-800 border-slate-700"
                      >
                        <div className="text-3xl font-bold text-white">{notifications.length}</div>
                        <p className="text-gray-400 text-sm">
                          {notifications.filter(n => !n.read).length} unread
                        </p>
                      </Card>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                      <Card
                        title="Recent Saved Cases"
                        icon={<Bookmark className="h-5 w-5 text-blue-400" />}
                        className="bg-slate-800 border-slate-700"
                      >
                        {savedCases.length > 0 ? (
                          <ul className="divide-y divide-slate-700">
                            {savedCases.slice(0, 3).map(item => (
                              <li key={item.id} className="py-2">
                                <div className="flex justify-between">
                                  <div>
                                    <p className="text-white font-medium">{item.title}</p>
                                    <p className="text-xs text-gray-400">{item.circuit} • Saved on {item.date}</p>
                                  </div>
                                  <button
                                    onClick={() => setActiveTab('saved')}
                                    className="text-blue-400 hover:text-blue-300 text-sm"
                                  >
                                    View
                                  </button>
                                </div>
                              </li>
                            ))}
                          </ul>
                        ) : (
                          <p className="text-gray-400 py-4 text-center">No saved cases yet</p>
                        )}

                        <div className="mt-2">
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => setActiveTab('saved')}
                            fullWidth
                          >
                            View All Saved Cases
                          </Button>
                        </div>
                      </Card>

                      <Card
                        title="Recent Notifications"
                        icon={<Bell className="h-5 w-5 text-blue-400" />}
                        className="bg-slate-800 border-slate-700"
                      >
                        {notifications.length > 0 ? (
                          <ul className="divide-y divide-slate-700">
                            {notifications.slice(0, 3).map(item => (
                              <li key={item.id} className="py-2">
                                <div className="flex">
                                  <div className={`flex-shrink-0 w-2 h-2 rounded-full mt-2 mr-2 ${
                                    item.read ? 'bg-transparent' : 
                                    item.type === 'alert' ? 'bg-red-500' : 
                                    item.type === 'update' ? 'bg-yellow-500' : 
                                    'bg-blue-500'
                                  }`} />
                                  <div>
                                    <p className={`font-medium ${item.read ? 'text-gray-400' : 'text-white'}`}>
                                      {item.message}
                                    </p>
                                    <p className="text-xs text-gray-500">{item.date}</p>
                                  </div>
                                </div>
                              </li>
                            ))}
                          </ul>
                        ) : (
                          <p className="text-gray-400 py-4 text-center">No notifications</p>
                        )}

                        <div className="mt-2">
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => setActiveTab('notifications')}
                            fullWidth
                          >
                            View All Notifications
                          </Button>
                        </div>
                      </Card>
                    </div>
                  </div>
                )}

                {activeTab === 'saved' && (
                  <div>
                    <h2 className="text-2xl font-bold text-white mb-6">Saved Cases</h2>

                    {savedCases.length > 0 ? (
                      <div className="bg-slate-800 border border-slate-700 rounded-lg overflow-hidden">
                        <ul className="divide-y divide-slate-700">
                          {savedCases.map(item => (
                            <li key={item.id} className="p-4">
                              <div className="flex justify-between items-center">
                                <div>
                                  <h3 className="text-lg font-medium text-white">{item.title}</h3>
                                  <p className="text-sm text-gray-400">{item.circuit} • Saved on {item.date}</p>
                                </div>
                                <div className="flex space-x-2">
                                  <Button
                                    variant="ghost"
                                    size="sm"
                                    onClick={() => {}}
                                  >
                                    View Case
                                  </Button>
                                  <Button
                                    variant="danger"
                                    size="sm"
                                    onClick={() => handleRemoveSavedCase(item.id)}
                                  >
                                    Remove
                                  </Button>
                                </div>
                              </div>
                            </li>
                          ))}
                        </ul>
                      </div>
                    ) : (
                      <div className="bg-slate-800 border border-slate-700 rounded-lg p-8 text-center">
                        <BookOpen className="h-12 w-12 mx-auto text-gray-500 mb-4" />
                        <h3 className="text-lg font-medium text-white mb-2">No saved cases</h3>
                        <p className="text-gray-400 mb-4">You haven't saved any cases yet.</p>
                        <Button
                          variant="primary"
                          onClick={() => {}}
                        >
                          Browse Cases
                        </Button>
                      </div>
                    )}
                  </div>
                )}

                {activeTab === 'history' && (
                  <div>
                    <div className="flex justify-between items-center mb-6">
                      <h2 className="text-2xl font-bold text-white">Search History</h2>
                      {recentSearches.length > 0 && (
