                  </div>
                  <div className="flex items-center">
                    <ExternalLink className={`h-4 w-4 mr-2 ${darkMode ? 'text-blue-400' : 'text-blue-600'}`} />
                    <a 
                      href={agency.website} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className={`text-sm ${darkMode ? 'text-blue-400 hover:text-blue-300' : 'text-blue-600 hover:text-blue-700'}`}
                    >
                      Visit Website
                    </a>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </Section>

        {/* State Constitution */}
        <Section id="constitution" title="State Constitutional Provisions" icon={Scale}>
          <ul className="space-y-2">
            {stateData.constitution?.provisions?.map((provision, index) => (
              <li key={index} className={`flex items-start ${darkMode ? 'text-white/80' : 'text-slate-700'}`}>
                <span className={`mr-2 ${darkMode ? 'text-purple-400' : 'text-purple-600'}`}>•</span>
                <span>{provision}</span>
              </li>
            ))}
          </ul>
        </Section>

        {/* Recent Developments */}
        <Section id="developments" title="Recent Developments" icon={Info}>
          <ul className="space-y-2">
            {stateData.recentDevelopments?.map((development, index) => (
              <li key={index} className={`flex items-start ${darkMode ? 'text-white/80' : 'text-slate-700'}`}>
                <span className={`mr-2 ${darkMode ? 'text-blue-400' : 'text-blue-600'}`}>•</span>
                <span>{development}</span>
              </li>
            ))}
          </ul>
        </Section>
      </div>

      {/* Footer */}
      <div className={`mt-6 p-4 rounded-lg ${darkMode ? 'bg-white/5' : 'bg-slate-50'} border ${darkMode ? 'border-white/10' : 'border-slate-200'}`}>
        <p className={`text-xs ${darkMode ? 'text-white/50' : 'text-slate-500'} italic`}>
          This information is provided for educational purposes and should not be considered legal advice. 
          For specific legal guidance, please consult with a qualified attorney in your jurisdiction.
        </p>
      </div>
    </div>
  );
};

export default DetailedStateProfile;

src/services/LegislativeTrackerAPI.js.content:
// Real-time legislative tracking service (via backend proxy)
class LegislativeTrackerAPI {
  constructor() {
    // Use backend proxy to avoid exposing API keys
    this.baseURL = '/api/legislative';
  }

  // Get current bills by state
  async getBillsByState(state, session = '2025') {
    try {
      const response = await fetch(`${this.baseURL}/bills?state=${state}&session=${session}`);

      if (!response.ok) {
        if (response.status === 503) {
          console.warn('LegiScan API not configured');
          return [];
        }
        throw new Error(`API error: ${response.status}`);
      }

      const data = await response.json();
      return this.filterCivilRightsBills(data);
    } catch (error) {
      console.error('Error fetching bills:', error);
      return [];
    }
  }

  // Filter bills related to civil rights
  filterCivilRightsBills(data) {
    if (!data.masterlist) return [];

    const civilRightsKeywords = [
      'civil rights', 'discrimination', 'equality', 'voting rights', 'police reform',
      'criminal justice', 'marijuana', 'cannabis', 'drug policy', 'sentencing reform',
      'racial justice', 'LGBTQ', 'transgender', 'reproductive rights', 'privacy rights'
    ];

    return Object.values(data.masterlist).filter(bill => {
      const title = (bill.title || '').toLowerCase();
      const description = (bill.description || '').toLowerCase();
      return civilRightsKeywords.some(keyword => 
        title.includes(keyword) || description.includes(keyword)
      );
    });
  }

  // Get bill details
  async getBillDetails(billId) {
    try {
      const response = await fetch(`${this.baseURL}/bill/${billId}`);

      if (!response.ok) {
        if (response.status === 503) {
          console.warn('LegiScan API not configured');
          return null;
        }
        throw new Error(`API error: ${response.status}`);
      }

      const data = await response.json();
      return data.bill;
    } catch (error) {
      console.error('Error fetching bill details:', error);
      return null;
    }
  }

  // Subscribe to bill updates
  async subscribeToBillUpdates(billId, callback) {
    // Real-time bill tracking would require webhook or WebSocket setup
    // This functionality requires a valid API key and proper backend configuration
    console.log(`Bill updates subscription ready for bill ${billId} (requires API configuration)`);
  }
}

export default new LegislativeTrackerAPI();

