  // Get breaking WTP News alerts
  getBreakingNews() {
    const articles = this.getFeaturedArticles();
    return articles.slice(0, 3);
  }

  // Get all WTP News articles
  getAllArticles() {
    return this.getFeaturedArticles();
  }

  // Get articles by category
  getArticlesByCategory(category) {
    return this.getFeaturedArticles().filter(article => 
      article.category.toLowerCase().includes(category.toLowerCase())
    );
  }
}

export default new WTPNewsAPI();

src/components/UserProfile.tsx.content:
import { useState, useEffect } from 'react';
import { User, Mail, MessageCircle, FileText, Award, Calendar, MapPin, Edit2, Save, X, Shield, Star, TrendingUp } from 'lucide-react';
import { supabase } from '../lib/supabase';

interface UserStats {
  violations_reported: number;
  comments_posted: number;
  messages_sent: number;
  account_age_days: number;
  followers: number;
  following: number;
}

interface UserProfileProps {
  userId?: string;
  isOwnProfile?: boolean;
}

export default function UserProfile({ userId, isOwnProfile = false }: UserProfileProps) {
  const [profile, setProfile] = useState<any>(null);
  const [stats, setStats] = useState<UserStats>({
    violations_reported: 0,
    comments_posted: 0,
    messages_sent: 0,
    account_age_days: 0,
    followers: 0,
    following: 0,
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editForm, setEditForm] = useState({
    display_name: '',
    bio: '',
    role: 'user',
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadProfile();
  }, [userId]);

  const loadProfile = async () => {
    try {
      const targetUserId = userId || (await supabase.auth.getUser()).data.user?.id;
      if (!targetUserId) return;

      // Load profile
      const { data: profileData } = await supabase
        .from('user_profiles')
        .select('*')
        .eq('id', targetUserId)
        .single();

      if (profileData) {
        setProfile(profileData);
        setEditForm({
          display_name: profileData.display_name || '',
          bio: profileData.bio || '',
          role: profileData.role || 'user',
        });

        // Calculate account age
        const accountAge = Math.floor(
          (Date.now() - new Date(profileData.created_at).getTime()) / (1000 * 60 * 60 * 24)
        );

        // Load stats
        const [comments, messages, followers, following] = await Promise.all([
          supabase.from('comments').select('id', { count: 'exact' }).eq('user_id', targetUserId),
          supabase.from('private_messages').select('id', { count: 'exact' }).eq('sender_id', targetUserId),
          supabase.from('user_connections').select('id', { count: 'exact' }).eq('following_id', targetUserId),
          supabase.from('user_connections').select('id', { count: 'exact' }).eq('follower_id', targetUserId),
        ]);

        setStats({
          violations_reported: 0, // Will be loaded from violations table
          comments_posted: comments.count || 0,
          messages_sent: messages.count || 0,
          account_age_days: accountAge,
          followers: followers.count || 0,
          following: following.count || 0,
        });
      }
    } catch (error) {
      console.error('Error loading profile:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSaveProfile = async () => {
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) return;

      const { error } = await supabase
        .from('user_profiles')
        .update({
          display_name: editForm.display_name,
          bio: editForm.bio,
          role: editForm.role,
          updated_at: new Date().toISOString(),
        })
        .eq('id', user.id);

      if (error) throw error;

      setIsEditing(false);
      loadProfile();
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  const getContributorLevel = () => {
    const totalActivity = stats.violations_reported + stats.comments_posted + stats.messages_sent;

    if (totalActivity >= 100) return { level: 'Elite Contributor', color: 'text-purple-600', icon: '👑' };
    if (totalActivity >= 50) return { level: 'Expert Contributor', color: 'text-blue-600', icon: '⭐' };
    if (totalActivity >= 25) return { level: 'Active Contributor', color: 'text-green-600', icon: '🌟' };
    if (totalActivity >= 10) return { level: 'Regular Contributor', color: 'text-yellow-600', icon: '✨' };
    return { level: 'New Member', color: 'text-gray-600', icon: '🌱' };
  };

  const getRoleBadge = (role: string) => {
    const badges: Record<string, { label: string; color: string; icon: any }> = {
      admin: { label: 'Admin', color: 'bg-red-100 text-red-800', icon: Shield },
      moderator: { label: 'Moderator', color: 'bg-purple-100 text-purple-800', icon: Shield },
      attorney: { label: 'Civil Rights Attorney', color: 'bg-blue-100 text-blue-800', icon: Award },
      journalist: { label: 'Journalist', color: 'bg-green-100 text-green-800', icon: FileText },
      activist: { label: 'Activist', color: 'bg-orange-100 text-orange-800', icon: TrendingUp },
      user: { label: 'Member', color: 'bg-gray-100 text-gray-800', icon: User },
    };

    const badge = badges[role] || badges.user;
    const Icon = badge.icon;

    return (
      <span className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-semibold ${badge.color}`}>
        <Icon className="h-4 w-4 mr-1" />
        {badge.label}
      </span>
    );
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center p-12">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  if (!profile) {
    return (
      <div className="text-center p-12">
        <p className="text-gray-600">Profile not found</p>
      </div>
    );
  }

  const contributorLevel = getContributorLevel();
  const totalActivity = stats.violations_reported + stats.comments_posted + stats.messages_sent;

  return (
    <div className="max-w-4xl mx-auto p-6">
      {/* Profile Header */}
      <div className="bg-white rounded-lg shadow-lg overflow-hidden mb-6">
        <div className="bg-gradient-to-r from-blue-600 to-indigo-600 h-32"></div>
        <div className="px-6 pb-6">
          <div className="flex items-start justify-between -mt-16 mb-4">
            <div className="flex items-end space-x-4">
              <div className="h-32 w-32 rounded-full border-4 border-white bg-gray-200 flex items-center justify-center text-4xl shadow-lg">
                {profile.avatar_url ? (
                  <img src={profile.avatar_url} alt={profile.display_name} className="rounded-full" />
                ) : (
                  <User className="h-16 w-16 text-gray-400" />
                )}
              </div>
              <div className="mb-2">
                <h1 className="text-3xl font-bold text-gray-900">{profile.display_name || profile.username}</h1>
                <p className="text-gray-600">@{profile.username}</p>
              </div>
            </div>
            {isOwnProfile && !isEditing && (
              <button
                onClick={() => setIsEditing(true)}
                className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center space-x-2"
              >
                <Edit2 className="h-4 w-4" />
                <span>Edit Profile</span>
              </button>
            )}
          </div>

          {/* Edit Form */}
          {isEditing ? (
            <div className="bg-gray-50 rounded-lg p-6 mb-6">
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Display Name</label>
                  <input
                    type="text"
                    value={editForm.display_name}
                    onChange={(e) => setEditForm({ ...editForm, display_name: e.target.value })}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Bio</label>
                  <textarea
                    value={editForm.bio}
                    onChange={(e) => setEditForm({ ...editForm, bio: e.target.value })}
                    rows={4}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="Tell us about yourself..."
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Role</label>
                  <select
                    value={editForm.role}
                    onChange={(e) => setEditForm({ ...editForm, role: e.target.value })}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  >
                    <option value="user">Member</option>
                    <option value="activist">Activist</option>
                    <option value="journalist">Journalist</option>
                    <option value="attorney">Civil Rights Attorney</option>
                  </select>
                </div>
                <div className="flex space-x-2">
                  <button
                    onClick={handleSaveProfile}
                    className="flex-1 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 flex items-center justify-center space-x-2"
                  >
                    <Save className="h-4 w-4" />
                    <span>Save Changes</span>
                  </button>
                  <button
                    onClick={() => setIsEditing(false)}
                    className="px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50 flex items-center space-x-2"
                  >
                    <X className="h-4 w-4" />
                    <span>Cancel</span>
                  </button>
                </div>
              </div>
            </div>
          ) : (
            <>
              {/* Badges */}
              <div className="flex flex-wrap gap-2 mb-4">
                {getRoleBadge(profile.role)}
                {profile.is_verified && (
                  <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-semibold bg-blue-100 text-blue-800">
                    <Star className="h-4 w-4 mr-1 fill-current" />
                    Verified
                  </span>
                )}
              </div>

              {/* Bio */}
              {profile.bio && (
                <p className="text-gray-700 mb-4">{profile.bio}</p>
              )}

              {/* Contributor Level */}
              <div className="bg-gradient-to-r from-yellow-50 to-orange-50 border-2 border-orange-200 rounded-lg p-4 mb-4">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm text-gray-600 mb-1">Contributor Level</p>
                    <p className={`text-2xl font-bold ${contributorLevel.color}`}>
                      {contributorLevel.icon} {contributorLevel.level}
                    </p>
                  </div>
                  <div className="text-right">
                    <p className="text-sm text-gray-600">Total Activity</p>
                    <p className="text-3xl font-bold text-gray-900">{totalActivity}</p>
                  </div>
                </div>
              </div>

              {/* Stats Grid */}
              <div className="grid grid-cols-2 md:grid-cols-3 gap-4 mb-4">
                <div className="bg-gray-50 rounded-lg p-4">
                  <div className="flex items-center justify-between mb-2">
                    <FileText className="h-5 w-5 text-blue-600" />
                    <span className="text-2xl font-bold text-gray-900">{stats.violations_reported}</span>
                  </div>
                  <p className="text-sm text-gray-600">Violations Reported</p>
                </div>

                <div className="bg-gray-50 rounded-lg p-4">
                  <div className="flex items-center justify-between mb-2">
                    <MessageCircle className="h-5 w-5 text-green-600" />
                    <span className="text-2xl font-bold text-gray-900">{stats.comments_posted}</span>
                  </div>
                  <p className="text-sm text-gray-600">Comments Posted</p>
                </div>

                <div className="bg-gray-50 rounded-lg p-4">
                  <div className="flex items-center justify-between mb-2">
                    <Mail className="h-5 w-5 text-purple-600" />
                    <span className="text-2xl font-bold text-gray-900">{stats.messages_sent}</span>
                  </div>
                  <p className="text-sm text-gray-600">Messages Sent</p>
                </div>

                <div className="bg-gray-50 rounded-lg p-4">
                  <div className="flex items-center justify-between mb-2">
                    <User className="h-5 w-5 text-indigo-600" />
                    <span className="text-2xl font-bold text-gray-900">{stats.followers}</span>
                  </div>
                  <p className="text-sm text-gray-600">Followers</p>
                </div>

                <div className="bg-gray-50 rounded-lg p-4">
                  <div className="flex items-center justify-between mb-2">
                    <User className="h-5 w-5 text-pink-600" />
                    <span className="text-2xl font-bold text-gray-900">{stats.following}</span>
                  </div>
                  <p className="text-sm text-gray-600">Following</p>
                </div>

                <div className="bg-gray-50 rounded-lg p-4">
                  <div className="flex items-center justify-between mb-2">
                    <Calendar className="h-5 w-5 text-orange-600" />
                    <span className="text-2xl font-bold text-gray-900">{stats.account_age_days}</span>
                  </div>
                  <p className="text-sm text-gray-600">Days Active</p>
                </div>
              </div>

              {/* Account Info */}
              <div className="text-sm text-gray-500 space-y-1">
                <div className="flex items-center space-x-2">
                  <Calendar className="h-4 w-4" />
                  <span>Joined {new Date(profile.created_at).toLocaleDateString()}</span>
                </div>
                {profile.last_seen_at && (
                  <div className="flex items-center space-x-2">
                    <User className="h-4 w-4" />
                    <span>Last seen {new Date(profile.last_seen_at).toLocaleDateString()}</span>
                  </div>
                )}
              </div>
            </>
          )}
        </div>
      </div>

      {/* Achievement Badges */}
      <div className="bg-white rounded-lg shadow-lg p-6">
        <h2 className="text-xl font-bold text-gray-900 mb-4">Achievements</h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {stats.violations_reported >= 1 && (
            <div className="text-center p-4 bg-blue-50 rounded-lg">
              <div className="text-3xl mb-2">🎯</div>
              <p className="text-sm font-semibold text-gray-900">First Report</p>
            </div>
          )}
          {stats.comments_posted >= 10 && (
            <div className="text-center p-4 bg-green-50 rounded-lg">
              <div className="text-3xl mb-2">💬</div>
              <p className="text-sm font-semibold text-gray-900">Conversationalist</p>
            </div>
          )}
          {stats.account_age_days >= 30 && (
            <div className="text-center p-4 bg-purple-50 rounded-lg">
              <div className="text-3xl mb-2">🎂</div>
              <p className="text-sm font-semibold text-gray-900">Veteran</p>
            </div>
          )}
          {totalActivity >= 50 && (
            <div className="text-center p-4 bg-yellow-50 rounded-lg">
              <div className="text-3xl mb-2">🏆</div>
              <p className="text-sm font-semibold text-gray-900">Top Contributor</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

src/utils/pdfGenerator.js.content:
import { jsPDF } from 'jspdf';

/**
 * Generate a comprehensive Activist/Journalist Legal Toolkit PDF
 */
export function generateActivistToolkitPDF(state = null) {
  const doc = new jsPDF();
  const pageWidth = doc.internal.pageSize.getWidth();
  const pageHeight = doc.internal.pageSize.getHeight();
  const margin = 15;
  const lineHeight = 7;
  let yPos = margin;

  // Helper function to add new page if needed
  const checkPageBreak = (requiredSpace = 20) => {
    if (yPos + requiredSpace > pageHeight - margin) {
      doc.addPage();
      yPos = margin;
      return true;
    }
    return false;
  };

  // Helper to add text with wrapping
  const addWrappedText = (text, fontSize = 10, isBold = false) => {
    doc.setFontSize(fontSize);
    doc.setFont('helvetica', isBold ? 'bold' : 'normal');
    const lines = doc.splitTextToSize(text, pageWidth - 2 * margin);
    lines.forEach(line => {
      checkPageBreak();
      doc.text(line, margin, yPos);
      yPos += lineHeight;
    });
  };

  // COVER PAGE
  doc.setFillColor(200, 16, 46); // Red
  doc.rect(0, 0, pageWidth, 60, 'F');

  doc.setTextColor(255, 255, 255);
  doc.setFontSize(28);
  doc.setFont('helvetica', 'bold');
  doc.text('ACTIVIST & JOURNALIST', pageWidth / 2, 25, { align: 'center' });
  doc.text('LEGAL TOOLKIT', pageWidth / 2, 38, { align: 'center' });

  doc.setFontSize(14);
  doc.setFont('helvetica', 'normal');
  doc.text('Know Your Rights • Stay Safe • Document Everything', pageWidth / 2, 50, { align: 'center' });

  doc.setTextColor(0, 0, 0);
  yPos = 80;

  doc.setFontSize(12);
  doc.setFont('helvetica', 'italic');
  doc.text(`Generated: ${new Date().toLocaleDateString()}`, pageWidth / 2, yPos, { align: 'center' });
  yPos += 15;

  if (state) {
    doc.setFontSize(14);
    doc.setFont('helvetica', 'bold');
    doc.text(`State-Specific Guide: ${state}`, pageWidth / 2, yPos, { align: 'center' });
    yPos += 10;
  }

  doc.setFontSize(10);
  doc.text('Civil Rights Legal Toolkit • www.wtpnews.org', pageWidth / 2, yPos, { align: 'center' });

  // TABLE OF CONTENTS
  doc.addPage();
  yPos = margin;

  doc.setFontSize(20);
  doc.setFont('helvetica', 'bold');
  addWrappedText('TABLE OF CONTENTS', 20, true);
  yPos += 5;

  const toc = [
    '1. Know Your Rights Overview',
    '2. First Amendment Protections',
    '3. Fourth Amendment (Search & Seizure)',
    '4. Recording Police & Public Officials',
    '5. Stop and ID Laws',
    '6. Marijuana/Cannabis Laws',
    '7. FOIA & Public Records Requests',
    '8. Emergency Legal Resources',
    '9. Attorney Contact Information',
    '10. Documentation Best Practices'
  ];

  doc.setFontSize(11);
  doc.setFont('helvetica', 'normal');
  toc.forEach(item => {
    yPos += lineHeight;
    doc.text(item, margin + 5, yPos);
  });

  // SECTION 1: KNOW YOUR RIGHTS OVERVIEW
  doc.addPage();
  yPos = margin;

  doc.setFillColor(30, 58, 138);
  doc.rect(margin - 5, yPos - 5, pageWidth - 2 * margin + 10, 15, 'F');
  doc.setTextColor(255, 255, 255);
  doc.setFontSize(16);
  doc.setFont('helvetica', 'bold');
  doc.text('1. KNOW YOUR RIGHTS OVERVIEW', margin, yPos + 5);
  doc.setTextColor(0, 0, 0);
  yPos += 20;

  addWrappedText('As an activist, journalist, or concerned citizen, understanding your constitutional rights is critical for protecting yourself during interactions with law enforcement and government officials.', 11);
  yPos += 3;

  doc.setFont('helvetica', 'bold');
  addWrappedText('Key Constitutional Rights:', 11, true);

  const rights = [
    '• First Amendment: Freedom of speech, press, assembly, and petition',
    '• Fourth Amendment: Protection from unreasonable searches and seizures',
    '• Fifth Amendment: Right to remain silent, due process',
    '• Sixth Amendment: Right to an attorney',
    '• Fourteenth Amendment: Equal protection under the law'
  ];

  doc.setFont('helvetica', 'normal');
  rights.forEach(right => {
    addWrappedText(right, 10);
  });

  // SECTION 2: FIRST AMENDMENT
  checkPageBreak(40);
  yPos += 5;

  doc.setFillColor(30, 58, 138);
  doc.rect(margin - 5, yPos - 5, pageWidth - 2 * margin + 10, 15, 'F');
  doc.setTextColor(255, 255, 255);
  doc.setFontSize(16);
  doc.setFont('helvetica', 'bold');
  doc.text('2. FIRST AMENDMENT PROTECTIONS', margin, yPos + 5);
  doc.setTextColor(0, 0, 0);
  yPos += 20;

  addWrappedText('The First Amendment protects your right to speak, publish, assemble, and petition the government. This is the foundation of journalism and activism.', 11);
  yPos += 3;

  doc.setFont('helvetica', 'bold');
  addWrappedText('What You Can Do:', 11, true);

  const firstAmendment = [
    '✓ Record police in public (protected in all 50 states)',
    '✓ Protest on public sidewalks and parks',
    '✓ Criticize government officials',
    '✓ Publish truthful information obtained legally',
    '✓ Refuse to reveal confidential sources (limited shield protection)',
    '✓ Attend public meetings and government proceedings'
  ];

  doc.setFont('helvetica', 'normal');
  firstAmendment.forEach(item => {
    addWrappedText(item, 10);
  });

  yPos += 3;
  doc.setFont('helvetica', 'bold');
  addWrappedText('Important Limitations:', 11, true);

  const limitations = [
    '✗ Cannot interfere with police operations',
    '✗ Cannot trespass on private property',
    '✗ Cannot incite imminent lawless action',
    '✗ Cannot make true threats',
    '✗ Must comply with reasonable time/place/manner restrictions'
  ];

  doc.setFont('helvetica', 'normal');
  limitations.forEach(item => {
    addWrappedText(item, 10);
  });

  // SECTION 3: RECORDING POLICE
  doc.addPage();
  yPos = margin;

  doc.setFillColor(30, 58, 138);
  doc.rect(margin - 5, yPos - 5, pageWidth - 2 * margin + 10, 15, 'F');
  doc.setTextColor(255, 255, 255);
  doc.setFontSize(16);
  doc.setFont('helvetica', 'bold');
  doc.text('4. RECORDING POLICE & PUBLIC OFFICIALS', margin, yPos + 5);
  doc.setTextColor(0, 0, 0);
  yPos += 20;

  addWrappedText('The right to record police officers performing their duties in public is constitutionally protected. Federal courts across the country have consistently upheld this right.', 11);
  yPos += 3;

  doc.setFont('helvetica', 'bold');
  addWrappedText('Best Practices for Recording:', 11, true);

  const recordingTips = [
    '1. Announce you are recording (optional but recommended)',
    '2. Maintain a safe distance (typically 10-15 feet)',
    '3. Do not interfere with police operations',
    '4. Record continuously - do not stop until safe',
    '5. Stream to cloud storage when possible',
    '6. Do NOT delete footage if ordered (assert 1st & 4th Amendment)',
    '7. Request supervisor if threatened',
    '8. Get badge numbers and names',
    '9. Document the scene from multiple angles',
    '10. Note time, date, location, and weather conditions'
  ];

  doc.setFont('helvetica', 'normal');
  recordingTips.forEach(tip => {
    addWrappedText(tip, 10);
  });

  // SECTION 4: IF STOPPED BY POLICE
  checkPageBreak(40);
  yPos += 5;

  doc.setFillColor(220, 38, 38);
  doc.rect(margin - 5, yPos - 5, pageWidth - 2 * margin + 10, 15, 'F');
  doc.setTextColor(255, 255, 255);
  doc.setFontSize(16);
  doc.setFont('helvetica', 'bold');
  doc.text('IF YOU ARE STOPPED BY POLICE', margin, yPos + 5);
  doc.setTextColor(0, 0, 0);
  yPos += 20;

  const policeStopped = [
    '1. Stay calm and keep hands visible',
    '2. Ask "Am I free to go?" If yes, leave calmly',
    '3. Ask "Am I being detained?" If yes, ask why',
    '4. You can record the entire encounter',
    '5. Ask for badge numbers and names',
    '6. Do not consent to searches ("I do not consent to any searches")',
    '7. Remain silent: "I am invoking my Fifth Amendment right to remain silent"',
    '8. Request attorney: "I want to speak with my lawyer"',
    '9. Do not resist physically, even if arrest is unlawful',
    '10. Document everything afterwards - time, location, witnesses, injuries'
  ];

  doc.setFont('helvetica', 'bold');
  policeStopped.forEach(step => {
    addWrappedText(step, 10, step.includes('1.') || step.includes('7.') || step.includes('8.'));
  });

  // SECTION 5: EMERGENCY CONTACTS
  doc.addPage();
  yPos = margin;

  doc.setFillColor(220, 38, 38);
  doc.rect(margin - 5, yPos - 5, pageWidth - 2 * margin + 10, 15, 'F');
  doc.setTextColor(255, 255, 255);
  doc.setFontSize(16);
  doc.setFont('helvetica', 'bold');
  doc.text('8. EMERGENCY LEGAL RESOURCES', margin, yPos + 5);
  doc.setTextColor(0, 0, 0);
  yPos += 20;

  doc.setFont('helvetica', 'bold');
  addWrappedText('National Legal Hotlines:', 12, true);

  const hotlines = [
    'ACLU Legal Hotline: Varies by state - visit aclu.org',
    'National Lawyers Guild Hotline: Text "NLGNOW" to 73278',
    'Reporters Committee Hotline: (800) 336-4243',
    'Electronic Frontier Foundation: (415) 436-9333',
    'Press Freedom Hotline: (202) 795-9300'
  ];

  doc.setFont('helvetica', 'normal');
  hotlines.forEach(hotline => {
    addWrappedText(hotline, 10);
  });

  yPos += 5;
  doc.setFont('helvetica', 'bold');
  addWrappedText('What to Do If Arrested:', 12, true);

  const ifArrested = [
    '1. Invoke right to remain silent immediately',
    '2. Request attorney before answering ANY questions',
    '3. Do not sign anything without attorney present',
    '4. Do not discuss case with cellmates',
    '5. Write down everything you remember ASAP',
    '6. Request medical attention for any injuries',
    '7. Do not waive Miranda rights',
    '8. Contact family/attorney as soon as allowed'
  ];

  doc.setFont('helvetica', 'normal');
  ifArrested.forEach(step => {
    addWrappedText(step, 10);
  });

  // DOCUMENTATION SECTION
  doc.addPage();
  yPos = margin;

  doc.setFillColor(30, 58, 138);
  doc.rect(margin - 5, yPos - 5, pageWidth - 2 * margin + 10, 15, 'F');
  doc.setTextColor(255, 255, 255);
  doc.setFontSize(16);
  doc.setFont('helvetica', 'bold');
  doc.text('10. DOCUMENTATION BEST PRACTICES', margin, yPos + 5);
  doc.setTextColor(0, 0, 0);
  yPos += 20;

  addWrappedText('Proper documentation is crucial for legal action and accountability. Follow these guidelines to protect yourself and build your case.', 11);
  yPos += 3;

  const documentation = [
    '📸 Record video continuously during interactions',
    '📝 Write detailed notes immediately afterwards',
    '🕐 Document exact times, dates, locations',
    '👥 Get names and contact info of witnesses',
    '🏥 Photograph all injuries immediately',
    '📧 Send yourself timestamped emails with details',
    '☁️ Upload footage to cloud storage immediately',
    '📋 File FOIA requests for body cam, dash cam footage',
    '🚨 File formal complaints when appropriate',
    '⚖️ Consult attorney within 48 hours of serious incidents'
  ];

  doc.setFont('helvetica', 'normal');
  documentation.forEach(tip => {
    addWrappedText(tip, 10);
  });

  // FOOTER ON EACH PAGE
  const totalPages = doc.internal.getNumberOfPages();
  for (let i = 1; i <= totalPages; i++) {
    doc.setPage(i);
    doc.setFontSize(8);
    doc.setTextColor(128, 128, 128);
    doc.text('Civil Rights Legal Toolkit • www.wtpnews.org • BuildMyBot.ai', pageWidth / 2, pageHeight - 10, { align: 'center' });
    doc.text(`Page ${i} of ${totalPages}`, pageWidth - margin, pageHeight - 10, { align: 'right' });
  }

  // Save the PDF
  const filename = state 
    ? `Activist-Toolkit-${state.replace(/\s+/g, '-')}-${new Date().toISOString().split('T')[0]}.pdf`
    : `Activist-Journalist-Legal-Toolkit-${new Date().toISOString().split('T')[0]}.pdf`;

  doc.save(filename);

  return filename;
}

/**
 * Generate a quick reference ID card PDF
 */
export function generateIDCardPDF(state) {
  const doc = new jsPDF({
    orientation: 'landscape',
    unit: 'mm',
    format: [85.6, 53.98] // Credit card size
  });

  const width = doc.internal.pageSize.getWidth();
  const height = doc.internal.pageSize.getHeight();

  // Front of card
  doc.setFillColor(30, 58, 138);
  doc.rect(0, 0, width, height, 'F');

  doc.setTextColor(255, 255, 255);
  doc.setFontSize(14);
  doc.setFont('helvetica', 'bold');
  doc.text('KNOW YOUR RIGHTS', width / 2, 8, { align: 'center' });

  doc.setFontSize(7);
  doc.setFont('helvetica', 'normal');
  const rights = [
    '✓ Right to remain silent',
    '✓ Right to refuse searches',
    '✓ Right to record police',
    '✓ Right to an attorney'
  ];

  let y = 15;
  rights.forEach(right => {
    doc.text(right, 5, y);
    y += 5;
  });

  doc.setFontSize(6);
  doc.text(`State: ${state || 'All 50 States'}`, width / 2, height - 8, { align: 'center' });
  doc.text('www.wtpnews.org', width / 2, height - 4, { align: 'center' });

  // Back of card
  doc.addPage();
  doc.setFillColor(200, 16, 46);
  doc.rect(0, 0, width, height, 'F');

  doc.setTextColor(255, 255, 255);
  doc.setFontSize(10);
  doc.setFont('helvetica', 'bold');
  doc.text('IF STOPPED:', width / 2, 8, { align: 'center' });

  doc.setFontSize(6);
  doc.setFont('helvetica', 'normal');
  const steps = [
    '1. Ask: "Am I free to go?"',
    '2. "I do not consent to searches"',
    '3. "I invoke my right to remain silent"',
    '4. "I want to speak with my lawyer"',
    '5. Record the entire encounter',
    '6. Get badge numbers & names'
  ];

  y = 15;
  steps.forEach(step => {
    doc.text(step, 5, y);
    y += 6;
  });

  doc.setFontSize(5);
  doc.text('Do NOT physically resist. Assert rights verbally.', width / 2, height - 4, { align: 'center' });

  const filename = `Rights-Card-${state ? state.replace(/\s+/g, '-') : 'National'}.pdf`;
  doc.save(filename);

  return filename;
}

