import { useState, useEffect } from 'react';
import { Mail, Send, Inbox, Star, Trash2, Search, User, X } from 'lucide-react';
import { supabase } from '../lib/supabase';

export default function MessagingPanel() {
  const [messages, setMessages] = useState<any[]>([]);
  const [selectedMessage, setSelectedMessage] = useState<any>(null);
  const [view, setView] = useState<'inbox' | 'sent' | 'compose'>('inbox');
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [composeForm, setComposeForm] = useState({
    recipient_username: '',
    subject: '',
    message_text: '',
  });
  const [currentUser, setCurrentUser] = useState<any>(null);
  const [unreadCount, setUnreadCount] = useState(0);

  useEffect(() => {
    loadCurrentUser();
  }, []);

  useEffect(() => {
    if (currentUser) {
      loadMessages();
    }
  }, [view, currentUser]);

  const loadCurrentUser = async () => {
    const { data: { user } } = await supabase.auth.getUser();
    setCurrentUser(user);
  };

  const loadMessages = async () => {
    if (!currentUser) return;

    setLoading(true);
    try {
      let query = supabase
        .from('private_messages')
        .select(`
          *,
          sender:sender_id(username, display_name),
          recipient:recipient_id(username, display_name)
        `)
        .order('created_at', { ascending: false });

      if (view === 'inbox') {
        query = query.eq('recipient_id', currentUser.id);
      } else if (view === 'sent') {
        query = query.eq('sender_id', currentUser.id);
      }

      const { data, error } = await query;

      if (error) throw error;
      setMessages(data || []);

      // Update unread count
      if (view === 'inbox') {
        const unread = data?.filter(m => !m.is_read).length || 0;
        setUnreadCount(unread);
      }
    } catch (error) {
      console.error('Error loading messages:', error);
    } finally {
      setLoading(false);
    }
  };

  const sendMessage = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!currentUser) return;

    try {
      // Find recipient by username
      const { data: recipientData } = await supabase
        .from('user_profiles')
        .select('id')
        .eq('username', composeForm.recipient_username.toLowerCase())
        .single();

      if (!recipientData) {
        alert('User not found');
        return;
      }

      const { error } = await supabase
        .from('private_messages')
        .insert({
          sender_id: currentUser.id,
          recipient_id: recipientData.id,
          subject: composeForm.subject,
          message_text: composeForm.message_text,
        });

      if (error) throw error;

      setComposeForm({ recipient_username: '', subject: '', message_text: '' });
      setView('sent');
      loadMessages();
    } catch (error: any) {
      console.error('Error sending message:', error);
      alert(error.message || 'Failed to send message');
    }
  };

  const markAsRead = async (messageId: string) => {
    try {
      await supabase
        .from('private_messages')
        .update({ is_read: true })
        .eq('id', messageId);

      loadMessages();
    } catch (error) {
      console.error('Error marking as read:', error);
    }
  };

  const deleteMessage = async (messageId: string) => {
    if (!confirm('Delete this message?')) return;

    try {
      await supabase
        .from('private_messages')
        .delete()
        .eq('id', messageId);

      setSelectedMessage(null);
      loadMessages();
    } catch (error) {
      console.error('Error deleting message:', error);
    }
  };

  const filteredMessages = messages.filter(msg => {
    if (!searchTerm) return true;
    const search = searchTerm.toLowerCase();
    return (
      msg.subject?.toLowerCase().includes(search) ||
      msg.message_text?.toLowerCase().includes(search) ||
      msg.sender?.username?.toLowerCase().includes(search)
    );
  });

  return (
    <div className="flex h-screen bg-gray-50">
      {/* Sidebar */}
      <div className="w-64 bg-white border-r border-gray-200">
        <div className="p-4">
          <button
            onClick={() => setView('compose')}
            className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded-lg flex items-center justify-center space-x-2"
          >
            <Send className="h-4 w-4" />
            <span>New Message</span>
          </button>
        </div>

        <nav className="px-2">
          <button
            onClick={() => setView('inbox')}
            className={`w-full flex items-center space-x-3 px-3 py-2 rounded-lg mb-1 ${
              view === 'inbox' ? 'bg-blue-50 text-blue-600' : 'text-gray-700 hover:bg-gray-50'
            }`}
          >
            <Inbox className="h-5 w-5" />
            <span className="flex-1 text-left">Inbox</span>
            {unreadCount > 0 && (
              <span className="bg-blue-600 text-white text-xs font-bold px-2 py-1 rounded-full">
                {unreadCount}
              </span>
            )}
          </button>

          <button
            onClick={() => setView('sent')}
            className={`w-full flex items-center space-x-3 px-3 py-2 rounded-lg ${
              view === 'sent' ? 'bg-blue-50 text-blue-600' : 'text-gray-700 hover:bg-gray-50'
            }`}
          >
            <Send className="h-5 w-5" />
            <span>Sent</span>
          </button>
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {view === 'compose' ? (
          <div className="p-6">
            <h2 className="text-2xl font-bold text-gray-900 mb-6">New Message</h2>
            <form onSubmit={sendMessage} className="max-w-2xl space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  To (username)
                </label>
                <input
                  type="text"
                  value={composeForm.recipient_username}
                  onChange={(e) => setComposeForm({ ...composeForm, recipient_username: e.target.value })}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="username"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Subject
                </label>
                <input
                  type="text"
                  value={composeForm.subject}
                  onChange={(e) => setComposeForm({ ...composeForm, subject: e.target.value })}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="Message subject"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Message
                </label>
                <textarea
                  value={composeForm.message_text}
                  onChange={(e) => setComposeForm({ ...composeForm, message_text: e.target.value })}
                  rows={10}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="Write your message..."
                  required
                />
              </div>

              <div className="flex space-x-2">
                <button
                  type="submit"
                  className="bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-6 rounded-lg"
                >
                  Send Message
                </button>
                <button
                  type="button"
                  onClick={() => {
                    setComposeForm({ recipient_username: '', subject: '', message_text: '' });
                    setView('inbox');
                  }}
                  className="border border-gray-300 text-gray-700 hover:bg-gray-50 py-2 px-6 rounded-lg"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        ) : (
          <>
            {/* Search Bar */}
            <div className="p-4 bg-white border-b border-gray-200">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                <input
                  type="text"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="Search messages..."
                />
              </div>
            </div>

            {/* Messages List */}
            <div className="flex-1 overflow-hidden flex">
              <div className="w-96 bg-white border-r border-gray-200 overflow-y-auto">
                {loading ? (
                  <div className="flex items-center justify-center p-12">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
                  </div>
                ) : filteredMessages.length === 0 ? (
                  <div className="text-center p-12">
                    <Mail className="h-12 w-12 text-gray-300 mx-auto mb-4" />
                    <p className="text-gray-500">No messages</p>
                  </div>
                ) : (
                  <div>
                    {filteredMessages.map((message) => (
                      <div
                        key={message.id}
                        onClick={() => {
                          setSelectedMessage(message);
                          if (!message.is_read && view === 'inbox') {
                            markAsRead(message.id);
                          }
                        }}
                        className={`p-4 border-b border-gray-200 cursor-pointer hover:bg-gray-50 ${
                          selectedMessage?.id === message.id ? 'bg-blue-50' : ''
                        } ${!message.is_read && view === 'inbox' ? 'bg-blue-50/30' : ''}`}
                      >
                        <div className="flex items-start justify-between mb-1">
                          <div className="flex items-center space-x-2">
                            <User className="h-4 w-4 text-gray-400" />
                            <span className="font-semibold text-gray-900">
                              {view === 'inbox' 
                                ? message.sender?.display_name || message.sender?.username 
                                : message.recipient?.display_name || message.recipient?.username}
                            </span>
                          </div>
                          {!message.is_read && view === 'inbox' && (
                            <div className="h-2 w-2 bg-blue-600 rounded-full"></div>
                          )}
                        </div>
                        <p className="text-sm font-medium text-gray-900 mb-1">
                          {message.subject || '(No subject)'}
                        </p>
                        <p className="text-sm text-gray-600 line-clamp-2">{message.message_text}</p>
                        <p className="text-xs text-gray-400 mt-1">
                          {new Date(message.created_at).toLocaleDateString()}
                        </p>
                      </div>
                    ))}
                  </div>
                )}
              </div>

              {/* Message Detail */}
              <div className="flex-1 bg-white overflow-y-auto">
                {selectedMessage ? (
                  <div className="p-6">
                    <div className="flex items-start justify-between mb-6">
                      <div>
                        <h2 className="text-2xl font-bold text-gray-900 mb-2">
                          {selectedMessage.subject || '(No subject)'}
                        </h2>
                        <div className="flex items-center space-x-2 text-sm text-gray-600">
                          <User className="h-4 w-4" />
                          <span>
                            {view === 'inbox' ? 'From: ' : 'To: '}
                            {view === 'inbox'
                              ? selectedMessage.sender?.display_name || selectedMessage.sender?.username
                              : selectedMessage.recipient?.display_name || selectedMessage.recipient?.username}
                          </span>
                          <span>•</span>
                          <span>{new Date(selectedMessage.created_at).toLocaleString()}</span>
                        </div>
                      </div>
                      <div className="flex space-x-2">
                        {view === 'inbox' && (
                          <button
                            onClick={() => {
                              setComposeForm({
                                recipient_username: selectedMessage.sender?.username || '',
                                subject: `Re: ${selectedMessage.subject || ''}`,
                                message_text: `\n\n--- Original Message ---\n${selectedMessage.message_text}`,
                              });
                              setView('compose');
                            }}
                            className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg"
                            title="Reply"
                          >
                            <Mail className="h-5 w-5" />
                          </button>
                        )}
                        <button
                          onClick={() => deleteMessage(selectedMessage.id)}
                          className="p-2 text-red-600 hover:bg-red-50 rounded-lg"
                          title="Delete"
                        >
                          <Trash2 className="h-5 w-5" />
                        </button>
                      </div>
                    </div>
                    <div className="prose max-w-none">
                      <p className="whitespace-pre-wrap text-gray-700">{selectedMessage.message_text}</p>
                    </div>
                  </div>
                ) : (
                  <div className="flex items-center justify-center h-full">
                    <div className="text-center">
                      <Mail className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                      <p className="text-gray-500">Select a message to read</p>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

src/components/AuthModal.tsx.content:
import { useState } from 'react';
import { X, Mail, Lock, User, AlertCircle } from 'lucide-react';
import { supabase } from '../lib/supabase';

interface AuthModalProps {
  isOpen: boolean;
  onClose: () => void;
  onAuthSuccess?: () => void;
}

export default function AuthModal({ isOpen, onClose, onAuthSuccess }: AuthModalProps) {
  const [mode, setMode] = useState<'login' | 'signup'>('login');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [username, setUsername] = useState('');
  const [displayName, setDisplayName] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  if (!isOpen) return null;

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const { data, error } = await supabase.auth.signInWithPassword({
        email,
        password,
      });

      if (error) throw error;

      if (data.user) {
        onAuthSuccess?.();
        onClose();
      }
    } catch (err: any) {
      setError(err.message || 'Failed to login');
    } finally {
      setLoading(false);
    }
  };

  const handleSignup = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    if (!username || username.length < 3) {
      setError('Username must be at least 3 characters');
      setLoading(false);
      return;
    }

    try {
      // Create auth account
      const { data, error } = await supabase.auth.signUp({
        email,
        password,
        options: {
          data: {
            username,
            display_name: displayName || username,
          },
        },
      });

      if (error) throw error;

      if (data.user) {
        // Create user profile
        const { error: profileError } = await supabase
          .from('user_profiles')
          .insert({
            id: data.user.id,
            username,
            display_name: displayName || username,
          });

        if (profileError) {
          console.error('Profile creation error:', profileError);
        }

        setError('Account created! Please check your email to verify your account.');
        setTimeout(() => {
          setMode('login');
          setError('');
        }, 3000);
      }
    } catch (err: any) {
      setError(err.message || 'Failed to create account');
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleLogin = async () => {
    try {
      const { error } = await supabase.auth.signInWithOAuth({
        provider: 'google',
        options: {
          redirectTo: `${window.location.origin}/auth/callback`,
        },
      });

      if (error) throw error;
    } catch (err: any) {
      setError(err.message || 'Failed to login with Google');
    }
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-md mx-4">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b">
          <h2 className="text-2xl font-bold text-gray-900">
            {mode === 'login' ? 'Login' : 'Create Account'}
          </h2>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        {/* Body */}
        <div className="p-6">
          {error && (
            <div className={`mb-4 p-3 rounded-lg flex items-start space-x-2 ${
              error.includes('created') || error.includes('verify') 
                ? 'bg-green-50 text-green-800' 
                : 'bg-red-50 text-red-800'
            }`}>
              <AlertCircle className="h-5 w-5 flex-shrink-0 mt-0.5" />
              <p className="text-sm">{error}</p>
            </div>
          )}

          <form onSubmit={mode === 'login' ? handleLogin : handleSignup} className="space-y-4">
            {mode === 'signup' && (
              <>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Username *
                  </label>
                  <div className="relative">
                    <User className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                    <input
                      type="text"
                      value={username}
                      onChange={(e) => setUsername(e.target.value.toLowerCase().replace(/[^a-z0-9_]/g, ''))}
                      className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      placeholder="username"
                      required
                    />
                  </div>
                  <p className="text-xs text-gray-500 mt-1">Lowercase letters, numbers, and underscores only</p>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Display Name
                  </label>
                  <input
                    type="text"
                    value={displayName}
                    onChange={(e) => setDisplayName(e.target.value)}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="How you want to be shown"
                  />
                </div>
              </>
            )}

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Email *
              </label>
              <div className="relative">
                <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="your@email.com"
                  required
                />
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Password *
              </label>
              <div className="relative">
                <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  placeholder="••••••••"
                  required
                  minLength={6}
                />
              </div>
              {mode === 'signup' && (
                <p className="text-xs text-gray-500 mt-1">At least 6 characters</p>
              )}
            </div>

            <button
              type="submit"
              disabled={loading}
              className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded-lg transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {loading ? 'Please wait...' : mode === 'login' ? 'Login' : 'Create Account'}
            </button>
          </form>

          {/* Social Login */}
          <div className="mt-4">
            <div className="relative">
              <div className="absolute inset-0 flex items-center">
                <div className="w-full border-t border-gray-300"></div>
              </div>
              <div className="relative flex justify-center text-sm">
                <span className="px-2 bg-white text-gray-500">Or continue with</span>
              </div>
            </div>

            <button
              onClick={handleGoogleLogin}
              className="mt-4 w-full flex items-center justify-center space-x-2 border border-gray-300 rounded-lg py-2 px-4 hover:bg-gray-50 transition-colors"
            >
              <svg className="h-5 w-5" viewBox="0 0 24 24">
                <path fill="#4285F4" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                <path fill="#34A853" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                <path fill="#FBBC05" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                <path fill="#EA4335" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
              </svg>
              <span className="text-gray-700 font-medium">Google</span>
            </button>
          </div>

          {/* Toggle mode */}
          <div className="mt-6 text-center">
            <button
              onClick={() => {
                setMode(mode === 'login' ? 'signup' : 'login');
                setError('');
              }}
              className="text-blue-600 hover:text-blue-700 text-sm font-medium"
            >
              {mode === 'login' ? "Don't have an account? Sign up" : 'Already have an account? Login'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

src/components/NotificationsCenter.tsx.content:
import { useState, useEffect } from 'react';
import { Bell, Heart, MessageCircle, Share2, UserPlus, AtSign, CheckCheck, Trash2 } from 'lucide-react';
import { supabase } from '../lib/supabase';

export default function NotificationsCenter() {
  const [notifications, setNotifications] = useState<any[]>([]);
  const [filter, setFilter] = useState<'all' | 'unread'>('all');
  const [loading, setLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState<any>(null);

  useEffect(() => {
    loadCurrentUser();
  }, []);

  useEffect(() => {
    if (currentUser) {
      loadNotifications();
    }
  }, [currentUser, filter]);

  const loadCurrentUser = async () => {
    const { data: { user } } = await supabase.auth.getUser();
    if (user) {
      const { data: profile } = await supabase
        .from('user_profiles')
        .select('*')
        .eq('id', user.id)
        .single();
      setCurrentUser(profile);
    }
  };

  const loadNotifications = async () => {
    if (!currentUser) return;

    try {
      let query = supabase
        .from('notifications')
        .select(`
          *,
          from_user:from_user_id(username, display_name, role)
        `)
        .eq('user_id', currentUser.id)
        .order('created_at', { ascending: false })
        .limit(50);

      if (filter === 'unread') {
        query = query.eq('is_read', false);
      }

      const { data, error } = await query;

      if (error) throw error;
      setNotifications(data || []);
    } catch (error) {
      console.error('Error loading notifications:', error);
    } finally {
      setLoading(false);
    }
  };

  const markAsRead = async (notificationId: string) => {
    try {
      await supabase
        .from('notifications')
        .update({ is_read: true })
        .eq('id', notificationId);

      loadNotifications();
    } catch (error) {
      console.error('Error marking notification as read:', error);
    }
  };

  const markAllAsRead = async () => {
    try {
      await supabase
        .from('notifications')
        .update({ is_read: true })
        .eq('user_id', currentUser.id)
        .eq('is_read', false);

      loadNotifications();
    } catch (error) {
      console.error('Error marking all as read:', error);
    }
  };

  const deleteNotification = async (notificationId: string) => {
    try {
      await supabase
        .from('notifications')
        .delete()
        .eq('id', notificationId);

      setNotifications(notifications.filter(n => n.id !== notificationId));
    } catch (error) {
      console.error('Error deleting notification:', error);
    }
  };

  const getNotificationIcon = (type: string) => {
    const icons: Record<string, any> = {
      like: { icon: Heart, color: 'text-red-500', bg: 'bg-red-50' },
      comment: { icon: MessageCircle, color: 'text-blue-500', bg: 'bg-blue-50' },
      share: { icon: Share2, color: 'text-green-500', bg: 'bg-green-50' },
      follow: { icon: UserPlus, color: 'text-purple-500', bg: 'bg-purple-50' },
      mention: { icon: AtSign, color: 'text-orange-500', bg: 'bg-orange-50' },
      reply: { icon: MessageCircle, color: 'text-indigo-500', bg: 'bg-indigo-50' },
      message: { icon: MessageCircle, color: 'text-pink-500', bg: 'bg-pink-50' },
    };

    return icons[type] || { icon: Bell, color: 'text-gray-500', bg: 'bg-gray-50' };
  };

  const getNotificationText = (notification: any) => {
    const fromUser = notification.from_user?.display_name || notification.from_user?.username || 'Someone';

    switch (notification.type) {
      case 'like':
        return `${fromUser} liked your post`;
      case 'comment':
        return `${fromUser} commented on your post`;
      case 'share':
        return `${fromUser} shared your post`;
      case 'follow':
        return `${fromUser} started following you`;
      case 'mention':
        return `${fromUser} mentioned you in a post`;
      case 'reply':
        return `${fromUser} replied to your comment`;
      case 'message':
        return `${fromUser} sent you a message`;
      default:
        return notification.content || 'New notification';
    }
  };

  const unreadCount = notifications.filter(n => !n.is_read).length;

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-4xl mx-auto px-4 py-6">
        {/* Header */}
        <div className="mb-6">
          <h1 className="text-4xl font-bold text-gray-900 mb-2 flex items-center">
            <Bell className="h-10 w-10 mr-3 text-blue-600" />
            Notifications
          </h1>
          {unreadCount > 0 && (
            <p className="text-lg text-gray-600">
              You have {unreadCount} unread notification{unreadCount !== 1 ? 's' : ''}
            </p>
          )}
        </div>

        {/* Filters and Actions */}
        <div className="bg-white rounded-xl shadow-md p-4 mb-6">
          <div className="flex items-center justify-between">
            <div className="flex space-x-2">
              <button
                onClick={() => setFilter('all')}
                className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                  filter === 'all'
                    ? 'bg-blue-600 text-white'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                All
              </button>
              <button
                onClick={() => setFilter('unread')}
                className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                  filter === 'unread'
                    ? 'bg-blue-600 text-white'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                Unread {unreadCount > 0 && `(${unreadCount})`}
              </button>
            </div>

            {unreadCount > 0 && (
              <button
                onClick={markAllAsRead}
                className="flex items-center space-x-2 text-blue-600 hover:text-blue-700 font-medium"
              >
                <CheckCheck className="h-5 w-5" />
                <span>Mark all as read</span>
              </button>
            )}
          </div>
        </div>

        {/* Notifications List */}
        {loading ? (
          <div className="flex items-center justify-center py-12">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
          </div>
        ) : notifications.length === 0 ? (
          <div className="bg-white rounded-xl shadow-md p-12 text-center">
            <Bell className="h-16 w-16 text-gray-300 mx-auto mb-4" />
            <p className="text-gray-500 text-lg">
              {filter === 'unread' ? 'No unread notifications' : 'No notifications yet'}
            </p>
          </div>
        ) : (
          <div className="space-y-2">
            {notifications.map((notification) => {
              const iconData = getNotificationIcon(notification.type);
              const Icon = iconData.icon;

              return (
                <div
                  key={notification.id}
                  className={`bg-white rounded-xl shadow-md hover:shadow-lg transition-all p-4 ${
                    !notification.is_read ? 'border-l-4 border-blue-500' : ''
                  }`}
                >
                  <div className="flex items-start space-x-4">
                    {/* Icon */}
                    <div className={`flex-shrink-0 h-12 w-12 rounded-full ${iconData.bg} flex items-center justify-center`}>
                      <Icon className={`h-6 w-6 ${iconData.color}`} />
                    </div>

                    {/* Content */}
                    <div className="flex-1 min-w-0">
                      <p className="text-gray-900 font-medium">
                        {getNotificationText(notification)}
                      </p>
                      {notification.content && (
                        <p className="text-gray-600 text-sm mt-1 line-clamp-2">
                          {notification.content}
                        </p>
                      )}
                      <p className="text-gray-400 text-sm mt-1">
                        {new Date(notification.created_at).toLocaleString()}
                      </p>
                    </div>

                    {/* Actions */}
                    <div className="flex items-center space-x-2">
                      {!notification.is_read && (
                        <button
                          onClick={() => markAsRead(notification.id)}
                          className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg transition-colors"
                          title="Mark as read"
                        >
                          <CheckCheck className="h-5 w-5" />
                        </button>
                      )}
                      <button
                        onClick={() => deleteNotification(notification.id)}
                        className="p-2 text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                        title="Delete"
                      >
                        <Trash2 className="h-5 w-5" />
                      </button>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
}

src/components/MainNavigation.tsx.content:
import { useState, useEffect } from 'react';
import { Shield, Home, Users, MessageSquare, Bell, Search, MapPin, Scale, BookOpen, TrendingUp, Menu, X, User, LogOut, Camera } from 'lucide-react';
import { supabase } from '../lib/supabase';
import AuthModal from './AuthModal';

interface MainNavigationProps {
  currentPage: string;
  onNavigate: (page: string) => void;
}

export default function MainNavigation({ currentPage, onNavigate }: MainNavigationProps) {
  const [currentUser, setCurrentUser] = useState<any>(null);
  const [showAuth, setShowAuth] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [unreadNotifications, setUnreadNotifications] = useState(0);
  const [unreadMessages, setUnreadMessages] = useState(0);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    loadCurrentUser();
  }, []);

  useEffect(() => {
    if (currentUser) {
      loadNotificationCounts();
    }
  }, [currentUser]);

  const loadCurrentUser = async () => {
    const { data: { user } } = await supabase.auth.getUser();
    if (user) {
      const { data: profile } = await supabase
        .from('user_profiles')
        .select('*')
        .eq('id', user.id)
        .single();
      setCurrentUser(profile);
    }
  };

  const loadNotificationCounts = async () => {
    if (!currentUser) return;

    try {
      const [notifications, messages] = await Promise.all([
        supabase
          .from('notifications')
          .select('id', { count: 'exact' })
          .eq('user_id', currentUser.id)
          .eq('is_read', false),
        supabase
          .from('private_messages')
          .select('id', { count: 'exact' })
          .eq('recipient_id', currentUser.id)
          .eq('is_read', false)
      ]);

      setUnreadNotifications(notifications.count || 0);
      setUnreadMessages(messages.count || 0);
    } catch (error) {
      console.error('Error loading notification counts:', error);
    }
  };

  const handleLogout = async () => {
    await supabase.auth.signOut();
    setCurrentUser(null);
    onNavigate('home');
  };

  const navItems = [
    { id: 'home', label: 'Home', icon: Home },
    { id: 'feed', label: 'Feed', icon: MessageSquare },
    { id: 'network', label: 'Network', icon: Users },
    { id: 'press-pass', label: 'Press Pass', icon: Camera },
    { id: 'violations', label: 'Violations', icon: MapPin },
    { id: 'legal', label: 'Legal Tools', icon: Scale },
    { id: 'cases', label: 'Cases', icon: BookOpen },
    { id: 'tracker', label: 'Tracker', icon: TrendingUp },
  ];

  return (
    <>
      <nav className="bg-white border-b border-gray-200 sticky top-0 z-50 shadow-sm">
        <div className="max-w-7xl mx-auto px-4">
          <div className="flex items-center justify-between h-16">
            {/* Logo */}
            <button
              onClick={() => onNavigate('home')}
              className="flex items-center space-x-2 hover:opacity-80 transition-opacity"
            >
              <Shield className="h-8 w-8 text-blue-600" />
              <span className="text-xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent hidden sm:block">
                CRN
              </span>
            </button>

            {/* Desktop Navigation */}
            <div className="hidden md:flex items-center space-x-1">
              {navItems.map((item) => {
                const Icon = item.icon;
                const isActive = currentPage === item.id;
                return (
                  <button
                    key={item.id}
                    onClick={() => onNavigate(item.id)}
                    className={`flex items-center space-x-2 px-4 py-2 rounded-lg font-medium transition-colors ${
                      isActive
                        ? 'bg-blue-50 text-blue-600'
                        : 'text-gray-700 hover:bg-gray-50'
                    }`}
                  >
                    <Icon className="h-5 w-5" />
                    <span className="hidden lg:block">{item.label}</span>
                  </button>
                );
              })}
            </div>

            {/* Right Side Actions */}
            <div className="flex items-center space-x-2">
              {/* Search */}
              <div className="hidden md:block relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                <input
                  type="text"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  placeholder="Search..."
                  className="pl-10 pr-4 py-2 border border-gray-300 rounded-full focus:ring-2 focus:ring-blue-500 focus:border-transparent w-48 lg:w-64"
                />
              </div>

              {currentUser ? (
                <>
                  {/* Notifications */}
                  <button
                    onClick={() => onNavigate('notifications')}
                    className="relative p-2 text-gray-700 hover:bg-gray-50 rounded-full"
                  >
                    <Bell className="h-6 w-6" />
                    {unreadNotifications > 0 && (
                      <span className="absolute top-0 right-0 h-5 w-5 bg-red-500 text-white text-xs font-bold rounded-full flex items-center justify-center">
                        {unreadNotifications > 9 ? '9+' : unreadNotifications}
                      </span>
                    )}
                  </button>

                  {/* Messages */}
                  <button
                    onClick={() => onNavigate('messages')}
                    className="relative p-2 text-gray-700 hover:bg-gray-50 rounded-full"
                  >
                    <MessageSquare className="h-6 w-6" />
                    {unreadMessages > 0 && (
                      <span className="absolute top-0 right-0 h-5 w-5 bg-blue-500 text-white text-xs font-bold rounded-full flex items-center justify-center">
                        {unreadMessages > 9 ? '9+' : unreadMessages}
                      </span>
                    )}
                  </button>

                  {/* Profile Dropdown */}
                  <div className="relative group">
                    <button className="flex items-center space-x-2 p-2 hover:bg-gray-50 rounded-full">
                      <div className="h-8 w-8 rounded-full bg-gradient-to-br from-blue-500 to-purple-500 flex items-center justify-center text-white font-bold text-sm">
                        {(currentUser.display_name || currentUser.username)[0].toUpperCase()}
                      </div>
                    </button>

                    {/* Dropdown Menu */}
                    <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all">
                      <div className="p-3 border-b border-gray-100">
                        <div className="font-semibold text-gray-900">
                          {currentUser.display_name || currentUser.username}
                        </div>
                        <div className="text-sm text-gray-500">@{currentUser.username}</div>
                      </div>
                      <button
                        onClick={() => onNavigate('profile')}
                        className="w-full text-left px-4 py-2 hover:bg-gray-50 flex items-center space-x-2"
                      >
                        <User className="h-4 w-4" />
                        <span>My Profile</span>
                      </button>
                      <button
                        onClick={handleLogout}
                        className="w-full text-left px-4 py-2 hover:bg-gray-50 flex items-center space-x-2 text-red-600"
                      >
                        <LogOut className="h-4 w-4" />
                        <span>Logout</span>
                      </button>
                    </div>
                  </div>
                </>
              ) : (
                <button
                  onClick={() => setShowAuth(true)}
                  className="bg-gradient-to-r from-blue-600 to-purple-600 text-white px-6 py-2 rounded-full font-semibold hover:shadow-lg transition-all"
                >
                  Join
                </button>
              )}

              {/* Mobile Menu Button */}
              <button
                onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
                className="md:hidden p-2 text-gray-700"
              >
                {mobileMenuOpen ? <X className="h-6 w-6" /> : <Menu className="h-6 w-6" />}
              </button>
            </div>
          </div>

          {/* Mobile Menu */}
          {mobileMenuOpen && (
            <div className="md:hidden py-4 space-y-2 border-t border-gray-200">
              {/* Mobile Search */}
              <div className="relative px-2 mb-4">
                <Search className="absolute left-5 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                <input
                  type="text"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  placeholder="Search..."
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-full focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>

              {navItems.map((item) => {
                const Icon = item.icon;
                const isActive = currentPage === item.id;
                return (
                  <button
                    key={item.id}
                    onClick={() => {
                      onNavigate(item.id);
                      setMobileMenuOpen(false);
                    }}
                    className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg font-medium transition-colors ${
                      isActive
                        ? 'bg-blue-50 text-blue-600'
                        : 'text-gray-700 hover:bg-gray-50'
                    }`}
                  >
                    <Icon className="h-5 w-5" />
                    <span>{item.label}</span>
                  </button>
                );
              })}
            </div>
          )}
        </div>
      </nav>

      {/* Auth Modal */}
      <AuthModal
        isOpen={showAuth}
        onClose={() => setShowAuth(false)}
        onAuthSuccess={() => {
          setShowAuth(false);
          loadCurrentUser();
          onNavigate('feed');
        }}
      />
    </>
  );
}

src/components/CommentsSection.tsx.content:
import { useState, useEffect } from 'react';
import { MessageCircle, ThumbsUp, ThumbsDown, Send, Reply, Flag, User } from 'lucide-react';
import { supabase } from '../lib/supabase';

interface CommentsSectionProps {
  contentType: 'violation' | 'news' | 'case' | 'legislation' | 'post';
  contentId: string;
  title?: string;
}

export default function CommentsSection({ contentType, contentId, title }: CommentsSectionProps) {
  const [comments, setComments] = useState<any[]>([]);
  const [newComment, setNewComment] = useState('');
  const [replyingTo, setReplyingTo] = useState<string | null>(null);
  const [replyText, setReplyText] = useState('');
  const [currentUser, setCurrentUser] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [showAuthPrompt, setShowAuthPrompt] = useState(false);

  useEffect(() => {
    loadCurrentUser();
    loadComments();
  }, [contentType, contentId]);

  const loadCurrentUser = async () => {
    const { data: { user } } = await supabase.auth.getUser();
    setCurrentUser(user);
  };

  const loadComments = async () => {
    try {
      const { data, error } = await supabase
        .from('comments')
        .select(`
          *,
          user:user_id(username, display_name, role, is_verified),
          replies:comments!parent_comment_id(
            *,
            user:user_id(username, display_name, role, is_verified)
          )
        `)
        .eq('content_type', contentType)
        .eq('content_id', contentId)
        .is('parent_comment_id', null)
        .order('created_at', { ascending: false });

      if (error) throw error;
      setComments(data || []);
    } catch (error) {
      console.error('Error loading comments:', error);
    } finally {
      setLoading(false);
    }
  };

  const postComment = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!currentUser) {
      setShowAuthPrompt(true);
      return;
    }

    if (!newComment.trim()) return;

    try {
      const { error } = await supabase
        .from('comments')
        .insert({
          user_id: currentUser.id,
          content_type: contentType,
          content_id: contentId,
          comment_text: newComment,
        });

      if (error) throw error;

      setNewComment('');
      loadComments();
    } catch (error: any) {
      console.error('Error posting comment:', error);
      alert(error.message || 'Failed to post comment');
    }
  };

  const postReply = async (parentId: string) => {
    if (!currentUser) {
      setShowAuthPrompt(true);
      return;
    }

    if (!replyText.trim()) return;

    try {
      const { error } = await supabase
        .from('comments')
        .insert({
          user_id: currentUser.id,
          content_type: contentType,
          content_id: contentId,
          comment_text: replyText,
          parent_comment_id: parentId,
        });

      if (error) throw error;

      setReplyText('');
      setReplyingTo(null);
      loadComments();
    } catch (error: any) {
      console.error('Error posting reply:', error);
      alert(error.message || 'Failed to post reply');
    }
  };

  const voteComment = async (commentId: string, voteType: 'upvote' | 'downvote') => {
    if (!currentUser) {
      setShowAuthPrompt(true);
      return;
    }

    try {
      // Check if user already voted
      const { data: existingVote } = await supabase
        .from('comment_votes')
        .select('*')
        .eq('user_id', currentUser.id)
        .eq('comment_id', commentId)
        .single();

      if (existingVote) {
        // Update existing vote
        if (existingVote.vote_type === voteType) {
          // Remove vote if clicking same button
          await supabase
            .from('comment_votes')
            .delete()
            .eq('id', existingVote.id);
        } else {
          // Change vote
          await supabase
            .from('comment_votes')
            .update({ vote_type: voteType })
            .eq('id', existingVote.id);
        }
      } else {
        // Create new vote
        await supabase
          .from('comment_votes')
          .insert({
            user_id: currentUser.id,
            comment_id: commentId,
            vote_type: voteType,
          });
      }

      loadComments();
    } catch (error) {
      console.error('Error voting:', error);
    }
  };

  const flagComment = async (commentId: string) => {
    if (!currentUser) {
      setShowAuthPrompt(true);
      return;
    }

    if (!confirm('Flag this comment for moderation?')) return;

    try {
      await supabase
        .from('comments')
        .update({ is_flagged: true })
        .eq('id', commentId);

      alert('Comment flagged for review');
      loadComments();
    } catch (error) {
      console.error('Error flagging comment:', error);
    }
  };

  const getRoleBadge = (role: string) => {
    const badges: Record<string, { color: string; label: string }> = {
      admin: { color: 'bg-red-100 text-red-800', label: 'Admin' },
      moderator: { color: 'bg-purple-100 text-purple-800', label: 'Mod' },
      attorney: { color: 'bg-blue-100 text-blue-800', label: 'Attorney' },
      journalist: { color: 'bg-green-100 text-green-800', label: 'Journalist' },
      activist: { color: 'bg-orange-100 text-orange-800', label: 'Activist' },
    };

    if (!role || role === 'user') return null;

    const badge = badges[role];
    if (!badge) return null;

    return (
      <span className={`text-xs px-2 py-0.5 rounded-full font-semibold ${badge.color}`}>
        {badge.label}
      </span>
    );
  };

  const CommentItem = ({ comment, isReply = false }: { comment: any; isReply?: boolean }) => (
    <div className={`${isReply ? 'ml-12 mt-4' : 'mb-6'} ${comment.is_flagged ? 'opacity-50' : ''}`}>
      <div className="bg-white rounded-lg shadow p-4">
        <div className="flex items-start justify-between mb-2">
          <div className="flex items-center space-x-2">
            <div className="h-8 w-8 rounded-full bg-blue-100 flex items-center justify-center">
              <User className="h-4 w-4 text-blue-600" />
            </div>
            <div>
              <div className="flex items-center space-x-2">
                <span className="font-semibold text-gray-900">
                  {comment.user?.display_name || comment.user?.username || 'Anonymous'}
                </span>
                {comment.user?.is_verified && (
                  <span className="text-blue-600" title="Verified">✓</span>
                )}
                {getRoleBadge(comment.user?.role)}
              </div>
              <span className="text-xs text-gray-500">
                {new Date(comment.created_at).toLocaleDateString()}
              </span>
            </div>
          </div>
          {comment.is_flagged && (
            <span className="text-xs bg-yellow-100 text-yellow-800 px-2 py-1 rounded">
              Flagged
            </span>
          )}
        </div>

        <p className="text-gray-700 mb-3 whitespace-pre-wrap">{comment.comment_text}</p>

        <div className="flex items-center space-x-4 text-sm">
          <button
            onClick={() => voteComment(comment.id, 'upvote')}
            className="flex items-center space-x-1 text-gray-600 hover:text-green-600"
          >
            <ThumbsUp className="h-4 w-4" />
            <span>{comment.upvotes || 0}</span>
          </button>

          <button
            onClick={() => voteComment(comment.id, 'downvote')}
            className="flex items-center space-x-1 text-gray-600 hover:text-red-600"
          >
            <ThumbsDown className="h-4 w-4" />
            <span>{comment.downvotes || 0}</span>
          </button>

          {!isReply && (
            <button
              onClick={() => setReplyingTo(comment.id)}
              className="flex items-center space-x-1 text-gray-600 hover:text-blue-600"
            >
              <Reply className="h-4 w-4" />
              <span>Reply</span>
            </button>
          )}

          <button
            onClick={() => flagComment(comment.id)}
            className="flex items-center space-x-1 text-gray-600 hover:text-yellow-600"
          >
            <Flag className="h-4 w-4" />
          </button>
        </div>

        {replyingTo === comment.id && (
          <div className="mt-4">
            <textarea
              value={replyText}
              onChange={(e) => setReplyText(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              rows={3}
              placeholder="Write a reply..."
            />
            <div className="flex space-x-2 mt-2">
              <button
                onClick={() => postReply(comment.id)}
                className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium"
              >
                Post Reply
              </button>
              <button
                onClick={() => {
                  setReplyingTo(null);
                  setReplyText('');
                }}
                className="border border-gray-300 text-gray-700 hover:bg-gray-50 px-4 py-2 rounded-lg text-sm"
              >
                Cancel
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Replies */}
      {comment.replies && comment.replies.length > 0 && (
        <div className="mt-4">
          {comment.replies.map((reply: any) => (
            <CommentItem key={reply.id} comment={reply} isReply={true} />
          ))}
        </div>
      )}
    </div>
  );

  return (
    <div className="bg-gray-50 rounded-lg p-6">
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-xl font-bold text-gray-900 flex items-center">
          <MessageCircle className="h-6 w-6 mr-2" />
          Comments {comments.length > 0 && `(${comments.length})`}
        </h3>
      </div>

      {title && (
        <div className="mb-4 p-3 bg-white rounded-lg border-l-4 border-blue-500">
          <p className="text-sm text-gray-600">Discussing:</p>
          <p className="font-semibold text-gray-900">{title}</p>
        </div>
      )}

      {/* Post New Comment */}
      {showAuthPrompt ? (
        <div className="mb-6 p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
          <p className="text-yellow-800 font-semibold mb-2">Please login to comment</p>
          <p className="text-sm text-yellow-700">You need to create an account or login to post comments and interact with the community.</p>
          <button
            onClick={() => setShowAuthPrompt(false)}
            className="mt-2 text-blue-600 hover:text-blue-700 text-sm font-medium"
          >
            Dismiss
          </button>
        </div>
      ) : currentUser ? (
        <form onSubmit={postComment} className="mb-6">
          <div className="bg-white rounded-lg shadow p-4">
            <textarea
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              rows={4}
              placeholder="Share your thoughts..."
              required
            />
            <button
              type="submit"
              className="mt-2 bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-lg font-medium flex items-center space-x-2"
            >
              <Send className="h-4 w-4" />
              <span>Post Comment</span>
            </button>
          </div>
        </form>
      ) : (
        <div className="mb-6 p-4 bg-blue-50 border border-blue-200 rounded-lg">
          <p className="text-blue-800">Login to join the conversation</p>
        </div>
      )}

      {/* Comments List */}
      {loading ? (
        <div className="flex items-center justify-center p-12">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
        </div>
      ) : comments.length === 0 ? (
        <div className="text-center p-12">
          <MessageCircle className="h-12 w-12 text-gray-300 mx-auto mb-4" />
          <p className="text-gray-500">No comments yet. Be the first to comment!</p>
        </div>
      ) : (
        <div>
          {comments.map((comment) => (
            <CommentItem key={comment.id} comment={comment} />
          ))}
        </div>
      )}
    </div>
  );
}

src/components/PressPassGenerator.tsx.content:
import { useState, useRef } from 'react';
import { Camera, Download, Shield, AlertCircle, CheckCircle } from 'lucide-react';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import { supabase } from '../lib/supabase';

interface PressPassData {
  fullName: string;
  organization: string;
  role: string;
  city: string;
  state: string;
  phone: string;
  email: string;
  photo: string | null;
}

export default function PressPassGenerator() {
  const [formData, setFormData] = useState<PressPassData>({
    fullName: '',
    organization: '',
    role: 'Journalist',
    city: '',
    state: '',
    phone: '',
    email: '',
    photo: null
  });
  const [preview, setPreview] = useState(false);
  const [generating, setGenerating] = useState(false);
  const passRef = useRef<HTMLDivElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const handlePhotoUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setFormData({ ...formData, photo: reader.result as string });
      };
      reader.readAsDataURL(file);
    }
  };

  const generatePDF = async () => {
    if (!passRef.current) return;

    setGenerating(true);
    try {
      const canvas = await html2canvas(passRef.current, {
        scale: 3,
        backgroundColor: '#ffffff',
        logging: false
      });

      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF({
        orientation: 'landscape',
        unit: 'mm',
        format: [85.6, 53.98]
      });

      pdf.addImage(imgData, 'PNG', 0, 0, 85.6, 53.98);
      pdf.save(`press-pass-${formData.fullName.replace(/\s+/g, '-').toLowerCase()}.pdf`);
    } catch (error) {
      console.error('Error generating PDF:', error);
      alert('Error generating press pass. Please try again.');
    } finally {
      setGenerating(false);
    }
  };

  const downloadImage = async () => {
    if (!passRef.current) return;

    setGenerating(true);
    try {
      const canvas = await html2canvas(passRef.current, {
        scale: 3,
        backgroundColor: '#ffffff',
        logging: false
      });

      const link = document.createElement('a');
      link.download = `press-pass-${formData.fullName.replace(/\s+/g, '-').toLowerCase()}.png`;
      link.href = canvas.toDataURL('image/png');
      link.click();
    } catch (error) {
      console.error('Error generating image:', error);
      alert('Error generating press pass. Please try again.');
    } finally {
      setGenerating(false);
    }
  };

  const isFormValid = formData.fullName && formData.organization && formData.city && formData.state && formData.email;

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-black to-gray-900 py-12 px-4">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="text-center mb-12">
          <div className="flex items-center justify-center mb-4">
            <Shield className="h-16 w-16 text-blue-500 mr-3" />
            <h1 className="text-5xl md:text-6xl font-black bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
              FREE PRESS PASS
            </h1>
          </div>
          <p className="text-xl text-gray-300 mb-4">
            Generate Your Digital Press Credentials Instantly
          </p>
          <div className="flex items-center justify-center space-x-2 text-blue-400">
            <CheckCircle className="h-5 w-5" />
            <span>Protected by First Amendment</span>
          </div>
        </div>

        <div className="grid md:grid-cols-2 gap-8">
          {/* Form Section */}
          <div className="bg-white/5 backdrop-blur-sm border border-white/10 rounded-2xl p-8">
            <h2 className="text-2xl font-bold text-white mb-6">Your Information</h2>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Full Name *
                </label>
                <input
                  type="text"
                  value={formData.fullName}
                  onChange={(e) => setFormData({ ...formData, fullName: e.target.value })}
                  className="w-full bg-white/10 border border-white/20 rounded-lg px-4 py-3 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="John Doe"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  News Organization / Outlet *
                </label>
                <input
                  type="text"
                  value={formData.organization}
                  onChange={(e) => setFormData({ ...formData, organization: e.target.value })}
                  className="w-full bg-white/10 border border-white/20 rounded-lg px-4 py-3 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="Independent Media / CRN News"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Role *
                </label>
                <select
                  value={formData.role}
                  onChange={(e) => setFormData({ ...formData, role: e.target.value })}
                  className="w-full bg-white/10 border border-white/20 rounded-lg px-4 py-3 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="Journalist">Journalist</option>
                  <option value="Reporter">Reporter</option>
                  <option value="Photographer">Photographer</option>
                  <option value="Videographer">Videographer</option>
                  <option value="Correspondent">Correspondent</option>
                  <option value="Citizen Journalist">Citizen Journalist</option>
                  <option value="Blogger">Blogger</option>
                  <option value="Podcaster">Podcaster</option>
                  <option value="Documentary Filmmaker">Documentary Filmmaker</option>
                </select>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-300 mb-2">
                    City *
                  </label>
                  <input
                    type="text"
                    value={formData.city}
                    onChange={(e) => setFormData({ ...formData, city: e.target.value })}
                    className="w-full bg-white/10 border border-white/20 rounded-lg px-4 py-3 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="New York"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-300 mb-2">
                    State *
                  </label>
                  <input
                    type="text"
                    value={formData.state}
                    onChange={(e) => setFormData({ ...formData, state: e.target.value })}
                    className="w-full bg-white/10 border border-white/20 rounded-lg px-4 py-3 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="NY"
                    maxLength={2}
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Phone Number
                </label>
                <input
                  type="tel"
                  value={formData.phone}
                  onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                  className="w-full bg-white/10 border border-white/20 rounded-lg px-4 py-3 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="(555) 123-4567"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Email *
                </label>
                <input
                  type="email"
                  value={formData.email}
                  onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                  className="w-full bg-white/10 border border-white/20 rounded-lg px-4 py-3 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="journalist@example.com"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Photo (Recommended)
                </label>
                <div className="flex items-center space-x-4">
                  <button
                    onClick={() => fileInputRef.current?.click()}
                    className="flex items-center space-x-2 bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-lg transition-colors"
                  >
                    <Camera className="h-5 w-5" />
                    <span>Upload Photo</span>
                  </button>
                  {formData.photo && (
                    <span className="text-green-400 text-sm">✓ Photo uploaded</span>
                  )}
                </div>
                <input
                  ref={fileInputRef}
                  type="file"
                  accept="image/*"
                  onChange={handlePhotoUpload}
                  className="hidden"
                />
                <p className="text-xs text-gray-500 mt-2">
                  Passport-style photo recommended (head and shoulders, neutral background)
                </p>
              </div>

              <button
                onClick={() => setPreview(true)}
                disabled={!isFormValid}
                className={`w-full py-4 rounded-lg font-bold text-lg transition-all ${
                  isFormValid
                    ? 'bg-gradient-to-r from-blue-600 to-purple-600 hover:shadow-lg hover:shadow-purple-500/50 text-white'
                    : 'bg-gray-600 text-gray-400 cursor-not-allowed'
                }`}
              >
                Generate Press Pass
              </button>
            </div>

            {/* Disclaimer */}
            <div className="mt-6 bg-yellow-500/10 border border-yellow-500/30 rounded-lg p-4">
              <div className="flex items-start space-x-3">
                <AlertCircle className="h-5 w-5 text-yellow-400 mt-0.5" />
                <div className="text-sm text-yellow-300">
                  <strong>Important:</strong> This press pass is protected by the First Amendment and serves as a statement of your constitutional rights. It does not replace event-specific media credentials or provide legal immunity.
                </div>
              </div>
            </div>
          </div>

          {/* Preview Section */}
          <div className="bg-white/5 backdrop-blur-sm border border-white/10 rounded-2xl p-8">
            <h2 className="text-2xl font-bold text-white mb-6">Preview & Download</h2>

            {preview ? (
              <div className="space-y-6">
                {/* Press Pass Preview */}
                <div 
                  ref={passRef}
                  className="bg-white p-6 rounded-lg shadow-2xl"
                  style={{ width: '400px', height: '252px' }}
                >
                  {/* Header */}
                  <div className="flex items-center justify-between mb-4 pb-3 border-b-2 border-blue-600">
                    <div className="flex items-center space-x-2">
                      <Shield className="h-8 w-8 text-blue-600" />
                      <div>
                        <div className="text-2xl font-black text-blue-600">PRESS</div>
                        <div className="text-xs text-gray-600 -mt-1">FIRST AMENDMENT</div>
                      </div>
                    </div>
                    <div className="text-right">
                      <div className="text-xs font-bold text-gray-800">ISSUED</div>
                      <div className="text-xs text-gray-600">{new Date().toLocaleDateString()}</div>
                    </div>
                  </div>

                  {/* Content */}
                  <div className="flex space-x-4">
                    {/* Photo */}
                    <div className="flex-shrink-0">
                      {formData.photo ? (
                        <img 
                          src={formData.photo} 
                          alt="Photo" 
                          className="w-24 h-24 object-cover rounded border-2 border-gray-300"
                        />
                      ) : (
                        <div className="w-24 h-24 bg-gray-200 rounded border-2 border-gray-300 flex items-center justify-center">
                          <Camera className="h-8 w-8 text-gray-400" />
                        </div>
                      )}
                    </div>

                    {/* Details */}
                    <div className="flex-1">
                      <div className="mb-2">
                        <div className="text-lg font-black text-gray-900 uppercase leading-tight">
                          {formData.fullName}
                        </div>
                        <div className="text-sm font-bold text-blue-600">
                          {formData.role}
                        </div>
                      </div>

                      <div className="text-xs text-gray-700 space-y-1">
                        <div className="font-semibold">{formData.organization}</div>
                        <div>{formData.city}, {formData.state}</div>
                        {formData.phone && <div>{formData.phone}</div>}
                        <div className="text-blue-600">{formData.email}</div>
                      </div>
                    </div>
                  </div>

                  {/* Footer */}
                  <div className="mt-3 pt-3 border-t border-gray-300">
                    <div className="text-xs text-gray-600 text-center">
                      <strong>Protected by the First Amendment of the U.S. Constitution</strong>
                      <br />
                      Civil Rights Network • freepresspass.com
                    </div>
                  </div>
                </div>

                {/* Download Buttons */}
                <div className="space-y-3">
                  <button
                    onClick={generatePDF}
                    disabled={generating}
                    className="w-full flex items-center justify-center space-x-3 bg-gradient-to-r from-blue-600 to-purple-600 hover:shadow-lg hover:shadow-purple-500/50 text-white px-6 py-4 rounded-lg font-bold transition-all disabled:opacity-50"
                  >
                    <Download className="h-5 w-5" />
                    <span>{generating ? 'Generating...' : 'Download as PDF'}</span>
                  </button>

                  <button
                    onClick={downloadImage}
                    disabled={generating}
                    className="w-full flex items-center justify-center space-x-3 bg-white/10 border border-white/20 hover:bg-white/20 text-white px-6 py-4 rounded-lg font-bold transition-all disabled:opacity-50"
                  >
                    <Download className="h-5 w-5" />
                    <span>{generating ? 'Generating...' : 'Download as Image'}</span>
                  </button>

                  <button
                    onClick={() => setPreview(false)}
                    className="w-full text-gray-400 hover:text-white py-2 text-sm transition-colors"
                  >
                    ← Edit Information
                  </button>
                </div>

                {/* Instructions */}
                <div className="bg-blue-500/10 border border-blue-500/30 rounded-lg p-4">
                  <h3 className="font-bold text-blue-400 mb-2">How to Use Your Press Pass:</h3>
                  <ul className="text-sm text-gray-300 space-y-1">
                    <li>• Download and save to your phone</li>
                    <li>• Print and laminate for durability</li>
                    <li>• Display when covering public events</li>
                    <li>• Keep photo ID handy for verification</li>
                  </ul>
                </div>
              </div>
            ) : (
              <div className="flex items-center justify-center h-64 text-gray-400">
                <div className="text-center">
                  <Shield className="h-16 w-16 mx-auto mb-4 opacity-50" />
                  <p>Fill out the form to preview your press pass</p>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Info Section */}
        <div className="mt-12 bg-white/5 backdrop-blur-sm border border-white/10 rounded-2xl p-8">
          <h2 className="text-2xl font-bold text-white mb-4">About Press Freedom</h2>
          <div className="grid md:grid-cols-3 gap-6 text-gray-300">
            <div>
              <h3 className="font-bold text-blue-400 mb-2">First Amendment Rights</h3>
              <p className="text-sm">
                The First Amendment protects the freedom of the press for all who gather and disseminate news—not just traditional media outlets.
              </p>
            </div>
            <div>
              <h3 className="font-bold text-blue-400 mb-2">Citizen Journalism</h3>
              <p className="text-sm">
                Anyone can practice journalism. This includes bloggers, documentarians, podcasters, and community reporters covering issues that matter.
              </p>
            </div>
            <div>
              <h3 className="font-bold text-blue-400 mb-2">Know Your Rights</h3>
              <p className="text-sm">
                You have the right to record police in public, attend public meetings, and request public records. Always act professionally and lawfully.
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

